#include <stdio.h>
#include<string>
#include<iostream>
#include<vector>
#include<unordered_map>
#include<unordered_set>
using namespace std;
class Solution {
public:
    int l1,l2;
    bool checkInclusion(string s1, string s2) {
        
        l1 = s1.length();l2 = s2.length();
        if( (l1 > 10,000) && (l2 > 10,000))
            return false;
        unordered_multiset<char> ss1,temp;
        for(auto i : s1)
            ss1.insert(i);
        temp = ss1;
        int count = 0;
        for(auto i = 0 ; i < l2; i ++)
        {
            char c = s2[i];
            auto it = temp.find(c);
            if(it != temp.end())
            {
                temp.erase(it);
                count++;
                if(temp.empty() && (count == l1 ))
                   {
                       return true;
                   }
                
            }


            else if(ss1.find(c) != ss1.end()) // this is used to go back find prev occurance and start from there.
            {
                temp = ss1;
                count = 0;
                auto it3 = temp.find(c);
                //auto ii = &s1[i];
                auto cc = std::count(s1.begin(),s1.end(),c);
                //int cc=1;
                temp.erase(it3);
                count++;
                auto it2 = i-1;
                int x = 0;
                while(it2 >= 0)
                {
                    if(s2[it2] == c)
                    {
                        cc--;
                        if(cc == 0)
                        {
                            i = it2 +1+x;
                        break;
                        }
                        else
                            {
                                it3 = temp.find(s2[it2]);
                                temp.erase(it3);
                                count++;
                                it2--;
                                x++;}

                    }
                    else
                    {
                        it3 = temp.find(s2[it2]);
                        temp.erase(it3);
                        count++;
                        if(it2 != 0)
                        {
                            it2--;}
                        else
                        {
                            i = it2;
                            break;
                        }
                        //if(it2 == 0)
                        x++;

                    }
                }
                
                continue;
            }
                   else
                   {
                       count = 0;
                       temp = ss1;
                       continue;
                   }
        }
        return false;
        
    }
};
